


def swapThis(a):
    count = sum([x!=y for x,y in zip(a,sorted(a))])

    if(count ==0):
        return 0
    elif(count ==1):
        return 1
    else:
        return -1



def swapToSort(a):
    c = sum(x != y for x, y in zip(a, sorted(a)))
    return 0 if c == 0 else 1 if c == 2 else -1

